// If an error occurs, or no results are found, a nil is returned.
// In the case of an error, the error is returned in the context.
func (b *projectQueryBuilder) Get() *Project {
	results := b.Load()
	if results != nil && len(results) > 0 {
		obj := results[0]
		return obj
	} else {
		return nil
	}
}

