// Delete deletes the record from the database.
//
// Associated LeafLs will also be deleted since their RootLID fields are not nullable.
func (o *rootLBase) Delete(ctx context.Context) (err error) {
	if o == nil {
		return // allow deleting of a nil object to be a noop
	}
	if !o._restored {
		panic("Cannot delete a record that has no primary key value.")
	}
	d := Database()
	err = db.ExecuteTransaction(ctx, d, func() error {

		{
			objs, err := QueryLeafLs(ctx).
				Where(op.Equal(node.LeafL().RootLID(), o.id)).
				Load()
			if err != nil {
				return err
			}
			for _, obj := range objs {
				if err = obj.Delete(ctx); err != nil {
					return err
				}
			}
			o.revLeafLs.Clear()
		}

		return d.Delete(ctx, "root_l", map[string]any{"ID": o.id})
	})

	if err != nil {
		return err
	}
	broadcast.Delete(ctx, "goradd_unit", "root_l", fmt.Sprint(o.id))
	return
}

// deleteRootL deletes the RootL with primary key pk from the database
// and handles associated records.
func deleteRootL(ctx context.Context, pk string) error {
	if obj, err := LoadRootL(ctx, pk, node.RootL().PrimaryKey()); err != nil {
		return err
	} else if obj != nil {
		if err := obj.Delete(ctx); err != nil {
			return err
		}
	}
	return nil
}

// resetDirtyStatus resets the dirty status of every field in the object.
func (o *rootLBase) resetDirtyStatus() {
	o.idIsDirty = false
	o.nameIsDirty = false
	o.revLeafLsIsDirty = false

}

// IsDirty returns true if the object has been changed since it was read from the database or created.
// However, a new object that has a column with a default value will be automatically marked as dirty upon creation.
func (o *rootLBase) IsDirty() (dirty bool) {
	dirty = o.idIsDirty ||
		o.nameIsDirty

	dirty = dirty ||
		o.revLeafLsIsDirty

	for obj := range o.revLeafLs.ValuesIter() {
		dirty = dirty || obj.IsDirty()
	}

	return
}

