// Invalid fields and objects are returned as nil.
// Get can be used to retrieve a value by using the Identifier of a node.
func (o *rootLBase) Get(key string) interface{} {

	switch key {

	case "ID":
		if !o.idIsLoaded {
			return nil
		}
		return o.id

	case "Name":
		if !o.nameIsLoaded {
			return nil
		}
		return o.name

	case "GroLock":
		if !o.groLockIsLoaded {
			return nil
		}
		return o.groLock

	case "LeafLs":
		return o.revLeafLs.Values()

	}
	return nil
}

