// Delete deletes the record from the database.
//
// Associated LeafNls will have their RootNlID field set to NULL.
func (o *rootNlBase) Delete(ctx context.Context) (err error) {
	if o == nil {
		return // allow deleting of a nil object to be a noop
	}
	if !o._restored {
		panic("Cannot delete a record that has no primary key value.")
	}
	d := Database()
	err = db.ExecuteTransaction(ctx, d, func() error {

		{
			objs, err := QueryLeafNls(ctx).
				Where(op.Equal(node.LeafNl().RootNlID(), o.id)).
				Select(node.LeafNl().RootNlID()).
				Load()
			if err != nil {
				return err
			}
			for _, obj := range objs {
				obj.SetRootNlIDToNull()
				if err = obj.Save(ctx); err != nil {
					return err
				}
			}
			o.revLeafNls.Clear()
		}

		return d.Delete(ctx, "root_nl", "ID", o.id, "gro_lock", o.GroLock())
	})

	if err != nil {
		return err
	}
	broadcast.Delete(ctx, "goradd_unit", "root_nl", fmt.Sprint(o.id))
	return
}

// deleteRootNl deletes the RootNl with primary key pk from the database
// and handles associated records.
func deleteRootNl(ctx context.Context, pk string) error {
	d := db.GetDatabase("goradd_unit")
	err := db.ExecuteTransaction(ctx, d, func() error {
		if obj, err := LoadRootNl(ctx,
			pk,
			node.RootNl().PrimaryKey(),
			node.RootNl().GroLock(),
		); err != nil {
			return err
		} else if obj == nil {
			return db.NewRecordNotFoundError("root_nl", pk)
		} else {
			if err := obj.Delete(ctx); err != nil {
				return err
			}
		}
		return nil
	})
	return err
}

