// Delete deletes the record from the database.
//
// An associated {= rev.ReverseIdentifier()  will also be deleted since its RootUlID field is not nullable.
func (o *rootUlBase) Delete(ctx context.Context) (err error) {
	if o == nil {
		return // allow deleting of a nil object to be a noop
	}
	if !o._restored {
		panic("Cannot delete a record that has no primary key value.")
	}
	d := Database()
	err = db.ExecuteTransaction(ctx, d, func() error {

		{
			obj, err := QueryLeafUls(ctx).
				Where(op.Equal(node.LeafUl().RootUlID(), o.id)).
				Get()
			if err != nil {
				return err
			}
			if obj != nil {
				if err = obj.Delete(ctx); err != nil {
					return err
				}
			}
			// Set this object's pointer to the reverse object to nil to mark that we broke the link
			o.revLeafUl = nil
		}

		return d.Delete(ctx, "root_ul", "ID", o.id, "gro_lock", o.GroLock())
	})

	if err != nil {
		return err
	}
	broadcast.Delete(ctx, "goradd_unit", "root_ul", fmt.Sprint(o.id))
	return
}

// deleteRootUl deletes the RootUl with primary key pk from the database
// and handles associated records.
func deleteRootUl(ctx context.Context, pk string) error {
	d := db.GetDatabase("goradd_unit")
	err := db.ExecuteTransaction(ctx, d, func() error {
		if obj, err := LoadRootUl(ctx,
			pk,
			node.RootUl().PrimaryKey(),
			node.RootUl().GroLock(),
		); err != nil {
			return err
		} else if obj == nil {
			return db.NewRecordNotFoundError("root_ul", pk)
		} else {
			if err := obj.Delete(ctx); err != nil {
				return err
			}
		}
		return nil
	})
	return err
}

